package JavaBasics;

public class LoopsConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//1. for loop
		for (int i = 0; i <=10; i++)
		{
			System.out.println(i);
		}
	
		System.out.println("*****");
		
		//2. while loop
		
		int j=10;
		while (j<=100)
		{
			System.out.println(j);
			j=j+10;
		}
		//3. infinite 
		/*for (int i = 0; i <=10;)
		{
			System.out.println(i);
		}*/
	
		System.out.println("********");
		for(int k=10; k>=-10; k--){
			System.out.println(k);
		}
		
	}

}
